ll prime[Nmax];
int is_prime[Nmax];
int prime_cnt;
int n;
ll num[Nmax];
void get()
{
    for(int i=2;i<Nmax;i++)
        is_prime[i]=1;
    for(ll i=2;i<Nmax;i++)
    {
        if(is_prime[i])
            prime[++prime_cnt]=i;
        for(int j=1;j<=prime_cnt;j++)
        {
            if(i*prime[j]>=Nmax)
                break;
            is_prime[i*prime[j]]=0;
            if(i%prime[j]==0)
                break;
        }
    }
    for(int i=1;i<=prime_cnt;i++)
        num[prime[i]]=i-1;
}
int main()
{
    #ifdef test
    #endif
    int n;
    get();
    while(~scanf("%d",&n))
    {
        ll res=ans[n/10000];
        int now=n/10000*10000;
        for(int i=now+1;i<=n;i++)
        {
            if(is_prime[i])
            {
                res+=(ll)2*num[i];
            }
            else
            {
                int now=i;
                for(int j=1;j<=prime_cnt&& prime[j]<=now && prime[j]*prime[j] <=now;j++)     
                {
                    if(now%prime[j]==0)
                    {
                        while(now%prime[j]==0)
                            now/=prime[j];
                        res+=(ll)2*num[prime[j]];
                    }
                }
                if(now!=1)
                    res+=(ll)2*num[now];
            }
        }
        printf("%lld\n",res);
    }
    return 0;
}


